void CONTROLCLS::SetKnobValue(int ind, int val)
{
	if (slHns[ind]!=NULL)
	{
		
		changle[ind]=val*MAXPICTURES/MAXINTVALUE;
		gaa(slHns[ind],changle[ind]);

	} //else
      //MessageBox(0,"Knob desepeared","Check SetKnobValue member",0);
} ///
