HWND CONTROLCLS::add_knob(HWND hParent, int a,int b,int id)
{
	HWND hwnD;
      HDC hhDC; 
	char *cl_nm;
	char bbuf[10];
	cl_nm = (char*) malloc(32);
	memset(cl_nm ,0,32);
	itoa(id,&bbuf[0],10);
	strcat(cl_nm,CNTRLCLSNAME);
	strcat(cl_nm,&bbuf[0]);

    WNDCLASS wc_id;
    memset(&wc_id,0,sizeof(wc_id));
	wc_id.lpfnWndProc       =      HandleKnob;
	wc_id.style             =      0;
	wc_id.lpszMenuName      =      NULL;
	wc_id.hInstance         =      (HINSTANCE)  hInstance;
	wc_id.cbWndExtra        =      0;
	wc_id.hCursor           =      0;
	wc_id.hbrBackground     =      NULL;
	wc_id.lpszClassName     =      (LPSTR) cl_nm;///

  RegisterClass(&wc_id);

   hwnD = CreateWindow(
   (LPSTR) cl_nm, (LPSTR) NULL,
    WS_CHILD | WS_VISIBLE,
    a,b,BMPLENGH,BMPHIGH,
    (HWND)hParent,(HMENU) (int) id+500,
    (HINSTANCE)hInstance, NULL);
	if (cl_nm!=NULL) free(cl_nm);
    SetWindowLong(hwnD, GWL_USERDATA, (long) this);

		     /////// --------- EXCLUDE BACKGROUND PIXELS

	         if (BMPhsl==0)
			 {
                /////// ---------
			    hhDC = GetDC(hwnD);
			    unsigned char *bmparr;
			    bmparr=(unsigned char*)malloc(98304);
			    Decode(BITMAPARRAY,bmparr,20196);
                BMPhsl=extro_lbs(hhDC,(char*)bmparr);
	            ReleaseDC(hwnD, hhDC);
                free(bmparr);
			 }
     
slHns[id]=hwnD;
return hwnD;

}
