void CONTROLCLS::gaa(HWND hWin, int number)
{
BITMAP bm;
HDC hdc;
HDC hdcMemory;
GetObject(BMPhsl, sizeof(bm), &bm);
hdc = GetDC(hWin);
hdcMemory = CreateCompatibleDC(hdc);
SelectObject(hdcMemory, BMPhsl);

BitBlt(hdc,0,0,BMPLENGH,BMPHIGH, hdcMemory, BMPLENGH*(MAXPICTURES-number), 0,SRCCOPY);/////MASK ROUND KNOB

DeleteDC(hdcMemory);
/////////////////
ReleaseDC(hWin, hdc);
}
